package com.example.carstudio.Activities;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import com.example.carstudio.CustomClass.Car;
import com.example.carstudio.R;
import com.example.carstudio.RecyclerViewAdapter.DetailRecyclerViewAdapter;
import com.example.carstudio.RecyclerViewAdapter.MyRecyclerAdapter;

import java.util.ArrayList;

public class DetailActivity extends AppCompatActivity implements MyRecyclerAdapter.MyRecyclerEvent{

    private RecyclerView rvDetailList;
    private ArrayList<Car> carList;
    private RecyclerView.LayoutManager layoutManager;
    private DetailRecyclerViewAdapter myAdapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_detail);

        Car car = (Car) getIntent().getSerializableExtra("Car");


        rvDetailList = findViewById(R.id.rvDetailList);
        carList = new ArrayList<>();
        layoutManager = new LinearLayoutManager(this);
        myAdapter = new DetailRecyclerViewAdapter(this, carList);
        rvDetailList.setLayoutManager(layoutManager);
        rvDetailList.setAdapter(myAdapter);

        if (car != null)
        {

            switch (car.getCarID())
            {
                case 1:

                    Car c1 = new Car();

                    c1.setCarName("Gran Coupé");
                    c1.setCatImage("data:image/jpeg;base64,");
                    carList.add(c1);

                    break;
                case 2:

                    Car c2 = new Car();
                    Car car1 = new Car();

                    c2.setCarName("Gran Coupé");
                    c2.setCatImage("data:image/jpeg;base64,");
                    carList.add(c2);

                    car1.setCarName("Gran Coupé");
                    car1.setCatImage("data:image/jpeg;base64,");
                    carList.add(car1);

                    break;
                case 3:

                    Car c3 = new Car();

                    c3.setCarName("Gran Coupé");
                    c3.setCatImage("data:image/jpeg;base64,");
                    carList.add(c3);

                    break;

                case 4:

                    Car c4 = new Car();

                    c4.setCarName("Gran Coupé");
                    c4.setCatImage("data:image/jpeg;base64,");
                    carList.add(c4);

                    break;


            }
        }


    }

    @Override
    public void setOnItemClickListener(int position) {

    }
}